import React, { Component } from 'react'
import Avatar from 'react-avatar';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import Button from '@material-ui/core/Button';

import EditableInput from '../common/editableInput';

const Placeholder = ({step, instructions, fileUploadHandler, fileInput, fileSelectedHandler, fileInputRef}) => { 

    const styles = theme => ({
        button: {
          margin: theme.spacing.unit,
        },
        leftIcon: {
          marginRight: theme.spacing.unit,
        },
        rightIcon: {
          marginLeft: theme.spacing.unit,
        },
        iconSmall: {
          fontSize: 20,
        },
      });

  return(


    <div> 
      <div className="imgPost Card"> 
          <div className="controls"> 
              <div>   
                Step {step}
              </div>

              <div> 
                  <i className="material-icons">favorite_border</i> 
                  <i className="material-icons">redo</i>
              </div>
          </div>

          <div className="img_container">
                                <input ref={fileInputRef} style={{display: 'none'}}  type="file" onChange={fileSelectedHandler}/>
                                <div className="imgUploadBtn"  onClick={fileInput}> 
                                <span>
                                    <i className="material-icons">add_a_photo</i>
                                </span> </div>

                                <Button variant="contained" color="default" className={classes.button}>
                                    Upload
                                    <CloudUploadIcon className={classes.rightIcon} />
                                </Button>

                                <button onClick={fileUploadHandler}> File Submit </button>
         </div>

          <div className="instructionBox"> 
              <EditableInput description={instructions} />
          </div>
      </div>
    </div>


        )
    }

    Placeholder.propTypes = {
        step: PropTypes.string.isRequired,
        instructions: PropTypes.string.isRequired,
        fileUploadHandler: PropTypes.func.isRequired,
        fileInput: PropTypes.func.isRequired,
        fileSelectedHandler: PropTypes.func.isRequired,
        fileInputRef: PropTypes.func.isRequired,

      };


export default Placeholder;
