import React, { Component } from 'react';
import Avatar from 'react-avatar';

export default class TargetDetails extends Component {
    constructor() {
      super();
      this.state = {
        showMe : true
      }
      this.toggleDescription = this.toggleDescription.bind(this);

    }


   toggleDescription() {
    this.setState(state => ({ 
      showMe: !state.isToggleOn
    }))
   }


   DescriptionItem = () =>  {
    const descriptionSolid = (
      <div className="Description"> 
          <p>{this.props.description}</p>
          </div>
    );
    const descriptionEditable = (
      <div>
        <input value="sample input" />
      </div>
    );

      if(this.showMe){
        return(
          {descriptionSolid}
        )}else{
          return(
            {descriptionEditable}
          )
        }
      }

  render() {
    const TheDescriptionItem = this.DescriptionItem()




    return (
      <div className="TargetDetails">  
        <div> 
          <h3>{this.props.title}</h3>
        </div>

        <div className="Thumbnail"> 
        <img className="Card"  src={this.props.thumbnail} alt=""/> 
        </div>

      <div onClick={this.toggleDescription} className="Description"> 
          {/* <p>{this.props.description}</p> */}
          {TheDescriptionItem}
          </div>



        <div className="input-field col s12">
          <form onSubmit={this.props.handleCommentSubmit}>
          <i className="material-icons prefix">textsms</i>
          <input type="text" placeholder="Comment..." className="autocomplete" value={this.props.newComment} onChange={this.props.handleCommentSubmitChange}/>
          </form>

        </div>

      </div>
    )
  }
}
