import React, { Component } from 'react';
import button from '@material-ui/core';

export default class TargetDetails extends Component {
  constructor(props) {
    super(props);

    this.state = {
      editing: false,
      submitting: false,
      draftText: this.props.description,
      text: this.props.description,
      error: false,
    }

    this.success = this.success;
    this.failure = this.failure;
  }

    success = () => {
        this.setState({submitting: false});
    }


    failure = () => {
        this.setState({
            submitting: false,
            error: true
        });
    }


    handleClickOutside = (e) => {
        if (this.wrapperRef && !this.wrapperRef.contains(e.target)) {
            this.toggleEditing();
        }
    }

    handleCancel = () => {
        this.setState({draftText: this.state.text});
        this.toggleEditing();
    }

    toggleEditing = () => {
        // so we can close on outside click
        if (this.state.editing) {
            document.removeEventListener('mousedown', this.handleClickOutside);
        } else {
            document.addEventListener('mousedown', this.handleClickOutside);
        }

        this.setState({editing: !this.state.editing});
    }

    handleChange = (e) => {
        this.setState({draftText: e.target.value});
    }

  // actually submit the request etc.
    triggerSubmit = () => {
        this.setState({
            text: this.state.draftText,
            submitting: true,
        });
        // submit
        // this.props.submitCallback(this.state.draftText, this.success, this.failure);
    }


  // check for enter or cancel
    handleKeyDown = (e) => {
        const enter_key = 13;
        const escape_key = 27;

        if (e.keyCode == enter_key) {
        this.toggleEditing();
        this.triggerSubmit();
        } else if (e.keyCode == escape_key) {
        this.toggleEditing();
        }
    }


  render() {
    let controlLinks;
    let editing;
    let textOrInput;
    let error;

    if (this.state.error) {
      error = <span className="error">error!</span>
    }



    if (this.state.editing) {
      textOrInput = <textarea id={this.props.id}
      onChange={this.handleChange}
      onKeyDown={this.handleKeyDown}>
      {this.state.draftText}
      </textarea>


      controlLinks = (
        <div>
          <a className="edit-link blue-link" onClick={this.handleCancel} href="#">CANCEL</a>
        </div>
      )

      editing = 'editing';
    } else {
      textOrInput = <span onClick={this.toggleEditing} className="text">{this.state.text}</span>
      controlLinks = <div><a className="edit-link blue-link" onClick={this.toggleEditing} href="#">EDIT</a></div>
      editing = '';
    }

    return (
      <div className="TargetDetails">  
        <div> 
          <h3>{this.props.title}</h3>
        </div>

        <div className="Thumbnail"> 
        <img className="Card"  src={this.props.thumbnail} alt=""/> 
        </div>


        <div
        className={`${this.props.className} ${editing} editable-field`}
        ref={(node) => { this.wrapperRef = node; }}
      >
        {textOrInput}
        {error}
        {controlLinks}
      </div>

      <div className="Description"> 
          {/* <p>{this.props.description}</p> */}
          {/* {TheDescriptionItem} */}
          </div>



        <div className="input-field col s12">
          <form onSubmit={this.props.handleCommentSubmit}>
          <i className="material-icons prefix">textsms</i>
          <input type="text" placeholder="Comment..." className="autocomplete" value={this.props.newComment} onChange={this.props.handleCommentSubmitChange}/>
          </form>

        </div>

      </div>
    )
  }
}
