import React from 'react';

import LeftNav from '../../../components/LeftNav/profile';
import InfoCard from '../../../components/Infocard';
import TreeItem from '../../../components/TreeItem';


import Pageheader from '../../../components/PageHeader';
import TargetDetails from '../../../components/TargetDetails/profile';
import Pill from '../../../components/common/pill';
import Comment from '../../../components/comment/profile';

export default class ProfileHealth extends React.Component {

    constructor() {
        super();
        this.state = {
                selectedTitle: this.title,
                selectedSymptoms: this.selectedSymptoms,
                selectedTreatment: this.selectedTreatment,
                selectedMedications: this.selectedMedications,
                selectedResults: this.selectedResults,
                commentsMessage: this.commentsMessage,
                selectedComment: 0,
                data : [
                        {
                        title: 'Constant Headaches',
                        symptoms: '0 - Lorem ipsum dolor sit amet',
                        treatment: '0- Treatment message here',
                        medications: '0- Medications message here',
                        results: '0- Results message here',
                        comments: [
                        {
                            id: 0,
                            img: '100008343750912',
                            name: 'Sam Jones II',
                            thumbnail: 'https://photos.smugmug.com/Test/i-fwbctT5/0/1c13d36b/S/floridafamily-S.jpg',
                            comment: '0 - Lorem ipsum dolor sit amet.'
                        },
                        {
                            id: 1,
                            img: '100008343750912',
                            name: 'Sam Jones II',
                            thumbnail: 'https://photos.smugmug.com/Test/i-fwbctT5/0/1c13d36b/S/floridafamily-S.jpg',
                            comment: '0 - Lorem ipsum dolor sit amet.'
                            },
                        {
                            id: 2,
                            img: '100008343750912',
                            name: 'Sam Jones II',
                            thumbnail: 'https://photos.smugmug.com/Test/i-fwbctT5/0/1c13d36b/S/floridafamily-S.jpg',
                            comment: '0 - Lorem ipsum dolor sit amet.'
                        }
                      ] 
                     },
                     {
                        title: 'Pimples',
                        symptoms: '1 - Lorem ipsum dolor sit amet',
                        treatment: '1- Treatment message here',
                        medications: '1- Medications message here',
                        results: '1- Results message here',
                        comments: [
                        {
                            id: 0,
                            img: '100008343750912',
                            name: 'Sam Jones II',
                            thumbnail: 'https://photos.smugmug.com/Test/i-fwbctT5/0/1c13d36b/S/floridafamily-S.jpg',
                            comment: '1 - Lorem ipsum dolor sit amet.'
                        },
                        {
                            id: 1,
                            img: '100008343750912',
                            name: 'Sam Jones II',
                            thumbnail: 'https://photos.smugmug.com/Test/i-fwbctT5/0/1c13d36b/S/floridafamily-S.jpg',
                            comment: '1 - Lorem ipsum dolor sit amet.'
                            },
                        {
                            id: 2,
                            img: '100008343750912',
                            name: 'Sam Jones II',
                            thumbnail: 'https://photos.smugmug.com/Test/i-fwbctT5/0/1c13d36b/S/floridafamily-S.jpg',
                            comment: '1 - Lorem ipsum dolor sit amet.'
                        }
                      ] 
                     },
                    ]
                   }
                   this.state.selectedSymptoms = this.state.data[0].symptoms;
                   this.state.selectedTreatment = this.state.data[0].treatment;
                   this.state.selectedMedications = this.state.data[0].medications;
                   this.state.selectedResults = this.state.data[0].results;
                   this.state.commentsMessage = this.findComments(this.state.selectedComment);

            }


            ShowDetails(medical) {
                this.setState = {
                    selectedTitle: medical.title,
                    selectedSymptoms: medical.symptoms,
                    selectedTreatment: medical.treatment,
                    selectedMedications: medical.medications,
                    selectedResults: medical.results,
                    commentsMessage: this.findComments(medical.id),

                }
            }


            findComments(I) { 

                const CommentsData = this.state.data[I].comments;
                const PostComments = CommentsData.map(x => (
                        <Comment  
                        key={x.id}  
                        avatar={x.img} 
                        comment={x.comment} />
                ))

                return(
                    <div> 
                    {PostComments}
                    </div>
                )
            }


    render() {

        const Pills = this.state.data.map(medical => (
                <Pill 
                selected={'PillItem'} 
                text={medical.title}
                showDetails={this.ShowDetails.bind(this, medical)}
                />
        ));


      return (
        <div className="Wrapper"> 
        <div className="Grid_wrapper">
            <div className="Grid_2Col">
            <div className="Column_feed">
                <TreeItem img="100008343750912" name="Adam Donatelli" location="St. Petersburg" bornYear="1983"/>
                <LeftNav/>
                <InfoCard 
                    location='St. Petersburg'
                    age='35'
                    education='University of Arizona'
                    bornIn="Hobbs,NM"
                    />
            </div>


            <div> 
                <div className="HeaderWrapper"> 
                <Pageheader text="Health Watch" icon="local_hospital" /> 
                </div>

                <div className="Grid_2Col_Modified"> 
                    <div className="Column">
                        <div className="PillList">
                                {Pills}
                        </div>

                       <div className="Medical_card">
                           <div className="Title"> Symptoms </div>
                           <div className="Content">
                            <p>
                                {this.state.selectedSymptoms}
                            </p>
                           </div>
                       </div>

                       <div className="Medical_card">
                           <div className="Title"> Treatment Approach </div>
                           <div className="Content">
                            <p>
                            {this.state.selectedTreatment}
                            </p>
                           </div>
                       </div>

                       <div className="Medical_card">
                           <div className="Title"> Medications </div>
                           <div className="Content">
                            <p>
                            {this.state.selectedMedications}
                            </p>
                           </div>
                       </div>

                       <div className="Medical_card">
                           <div className="Title"> Results </div>
                           <div className="Content">
                            <p>
                            {this.state.selectedResults}
                            </p>
                           </div>
                       </div>
                    </div>

                    <div className="Column">
                        <TargetDetails 
                        thumbnail="" 
                        title="Contant Headaches" 
                        img="100008343750912" 
                        img2="1424683623"/>

                        {this.state.commentsMessage}

                    </div>
                </div>
            </div>
            </div>
        </div>
        </div>
        );
    }
}
