import React, { Component } from 'react';
import Avatar from 'react-avatar';

export default class TargetDetails extends Component {
      constructor() {
        super();
        this.state = {
          newComment: '',

        }
        this.handleCommentSubmitChange = this.handleCommentSubmitChange.bind(this);
        this.handleCommentSubmit = this.handleCommentSubmit.bind(this);
      }


      handleCommentSubmitChange(event) {
        this.setState({newComment: event.target.value});
        }

    handleCommentSubmit(event) {
        const {newComment} = this.state;

            alert('A name was submitted: ' + this.state.newComment);
            event.preventDefault();

            this.state.Data.push({
                id: 3,
                img: '100008343750912',
                comment: newComment,
            })
            console.log(this.state.Data);
    }  


  render() {
    return (
      <div className="TargetDetails">  
        <div> 
          <h3>{this.props.title}</h3>
        </div>

        <div className="Thumbnail"> 
        <img className="Card"  src={this.props.thumbnail} alt=""/> 
        </div>

        <div className="Description"> 
          <p>{this.props.description}</p>
          </div>


        <div className="input-field col s12">
        <form>
          {/* <form onSubmit={this.props.handleCommentSubmit.bind(this.props.newComment)}> */}
          <i className="material-icons prefix">textsms</i>
          <input type="text" placeholder="Comment..." className="autocomplete" value={this.props.newComment} onChange={this.props.handleCommentSubmitChange}/>
          </form>

        </div>

      </div>
    )
  }
}
