import React, { Component } from 'react'
import Avatar from 'react-avatar';
import PropTypes from 'prop-types';

import Menu_dropdown from '../common/menu_dropdown';

const Placeholder = ({step, instructions}) => { 


  return(


    <div> 
      <div className="imgPost Card"> 
          <div className="controls"> 
              <div>   
                Step {step}
              </div>

              <div> 
                  <i className="material-icons">favorite_border</i> 
                  <i className="material-icons">redo</i>
              </div>
          </div>

          <div className="img_container">
                                <input ref={fileInput => this.fileInput = fileInput} style={{display: 'none'}}  type="file" onChange={this.fileSelectedHandler}/>
                                <div className="imgUploadBtn"  onClick={() => this.fileInput.click()}> 
                                <span>
                                    <i className="material-icons">add_a_photo</i>
                                </span> </div>

                                <button onClick={this.fileUploadHandler}> File Submit </button>
         </div>

          <div className="instructionBox"> 
              <EditableInput description={instructions} />
          </div>
      </div>
    </div>


        )
    }

    Placeholder.propTypes = {
        step: PropTypes.string.isRequired,
        instructions: PropTypes.string.isRequired,
      };


export default Placeholder;
